/************************************************************
 * Assuming that the RESERVE phases were successful, the next
 * stage is indicated by the action value ACTION. This is used
 * to actually implement the set operation. However, this must
 * either be done into temporary (persistent) storage, or the
 * previous value stored similarly, in case any of the subsequent
 * ACTION calls fail.
 *
 * In your case, changes should be made to row_ctx. A copy of
 * the original row is in undo_ctx.
 */
void commandTable_set_action(netsnmp_request_group *rg)
{
    netsnmp_variable_list *var;
    commandTable_context *row_ctx = (commandTable_context *)rg->existing_row;
    commandTable_context *undo_ctx = (commandTable_context *)rg->undo_info;
    netsnmp_request_group_item *current;

    /*
     * TODO: loop through columns, copy varbind values
     * to context structure for the row.
     */
    for (current = rg->list; current; current = current->next)
    {

        var = current->ri->requestvb;

        switch (current->tri->colnum)
        {

        case COLUMN_COMMANDID:
            /** UNSIGNED32 = ASN_UNSIGNED */
            row_ctx->commandID = *var->val.integer;
            break;
        case COLUMN_TEMPLATEID:
            /** UNSIGNED32 = ASN_UNSIGNED */
            row_ctx->templateID = *var->val.integer;
            break;
        case COLUMN_COMMANDINPUT:
            /** INTEGER = ASN_INTEGER */
            row_ctx->commandInput = *var->val.integer;
            break;
        case COLUMN_COMMANDUSER:
            /** OCTET STRING = ASN_OCTET_STR */
            memcpy(row_ctx->commandUser, var->val.string, var->val_len);
            row_ctx->commandUser_len = var->val_len;
            break;
        default:               /** We shouldn't get here */
            netsnmp_assert(0); /** why wasn't this caught in reserve1? */
        }
    }

    /*
     * done with all the columns. Could check row related
     * requirements here.
     */
    /*
     * TODO: if you have dependencies on other tables, this would be
     * a good place to check those, too.
     */
}

