commandTable_context *commandTable_create_row(netsnmp_index *hdr)
{
    commandTable_context *ctx = SNMP_MALLOC_TYPEDEF(commandTable_context);
    if (!ctx)
        return NULL;
    if (commandTable_extract_index(ctx, hdr))
    {
        free(ctx->index.oids);
        free(ctx);
        return NULL;
    }
    ctx->commandUser_len = 0;
    return ctx;
}
