/************************************************************
 * Initializes the errorSensorTable module
 */
void init_errorSensorTable(void)
{
    errorSensorTable_context *ctx;
    netsnmp_index index;
    oid index_oid[2];
    initialize_table_errorSensorTable();
    errorSensorStruct *req = (errorSensorStruct *)malloc(sizeof(errorSensorStruct));
    req->errorSensorID = 0;
    req->errorSensorDescription = "Descricao sensor teste";
    index_oid[0] = 0;
    index.oids = (oid *)&index_oid;
    index.len = 1;
    ctx = NULL;
    /* Search for it first. */
    ctx = CONTAINER_FIND(cb.container, &index);
    if (!ctx)
    {
        // No dice. We add the new row
        ctx = errorSensorTable_create_row(&index, req);
        CONTAINER_INSERT(cb.container, ctx);
    }
}

