/************************************************************
 * the *_create_row routine is called by the table handler
 * to create a new row for a given index. If you need more
 * information (such as column values) to make a decision
 * on creating rows, you must create an initial row here
 * (to hold the column values), and you can examine the
 * situation in more detail in the *_set_reserve1 or later
 * states of set processing. Simple check for a NULL undo_ctx
 * in those states and do detailed creation checking there.
 *
 * returns a newly allocated errorSensorTable_context
 *   structure if the specified indexes are not illegal
 * returns NULL for errors or illegal index values.
 */
errorSensorTable_context *errorSensorTable_create_row(netsnmp_index *hdr, errorSensorStruct *req)
{
    errorSensorTable_context *ctx = SNMP_MALLOC_TYPEDEF(errorSensorTable_context);
    if (!ctx)
        return NULL;
    if (errorSensorTable_extract_index(ctx, hdr))
    {
        free(ctx->index.oids);
        free(ctx);
        return NULL;
    }
    ctx->index.oids = ctx->oid_buf;
    ctx->oid_buf[0] = req->errorSensorID;
    ctx->index.len = 1;
    ctx->errorSensorID = (long unsigned int)req->errorSensorID;
    strcpy(ctx->errorSensorDescription, req->errorSensorDescription);
    ctx->errorSensorDescription_len = strlen(req->errorSensorDescription);
    printf("ErrorSensorTable inserida: %ld\n", ctx->errorSensorID);
    return ctx;
}
