/************************************************************
 * the *_row_copy routine
 */
int errorTable_row_copy(errorTable_context *dst,
                        errorTable_context *src)
{
    if (!dst || !src)
        return 1;

    /*
     * copy index, if provided
     */
    if (dst->index.oids)
        free(dst->index.oids);
    if (snmp_clone_mem((void *)&dst->index.oids, src->index.oids,
                       src->index.len * sizeof(oid)))
    {
        dst->index.oids = NULL;
        return 1;
    }
    dst->index.len = src->index.len;

    /*
     * copy components into the context structure
     */
    dst->errorID = src->errorID;

    memcpy(dst->errorTimeStamp, src->errorTimeStamp, src->errorTimeStamp_len);
    dst->errorTimeStamp_len = src->errorTimeStamp_len;

    dst->errorDescriptionID = src->errorDescriptionID;

    memcpy(dst->errorTimeStamp, src->errorTimeStamp, src->errorTimeStamp_len);
    dst->errorTimeStamp_len = src->errorTimeStamp_len;

    memcpy(dst->errorUser, src->errorUser, src->errorUser_len);
    dst->errorUser_len = src->errorUser_len;

    memcpy(dst->errorExpireTime, src->errorExpireTime, src->errorExpireTime_len);
    dst->errorExpireTime_len = src->errorExpireTime_len;

    return 0;
}

/**
 * the *_extract_index routine
 *
 * This routine is called when a set request is received for an index
 * that was not found in the table container. Here, we parse the oid
 * in the the individual index components and copy those indexes to the
 * context. Then we make sure the indexes for the new row are valid.
 */
int errorTable_extract_index(errorTable_context *ctx, netsnmp_index *hdr)
{
    /*
     * temporary local storage for extracting oid index
     *
     * extract index uses varbinds (netsnmp_variable_list) to parse
     * the index OID into the individual components for each index part.
     */
    netsnmp_variable_list var_errorID;
    int err;

    /*
     * copy index, if provided
     */
    if (hdr)
    {
        netsnmp_assert(ctx->index.oids == NULL);
        if ((hdr->len > MAX_OID_LEN) ||
            snmp_clone_mem((void *)&ctx->index.oids, hdr->oids,
                           hdr->len * sizeof(oid)))
        {
            return -1;
        }
        ctx->index.len = hdr->len;
    }

    /*
     * initialize variable that will hold each component of the index.
     * If there are multiple indexes for the table, the variable_lists
     * need to be linked together, in order.
     */
    memset(&var_errorID, 0x00, sizeof(var_errorID));
    var_errorID.type = ASN_UNSIGNED; /* type hint for parse_oid_indexes */
                                     /** TODO: link this index to the next, or NULL for the last one */
#ifdef TABLE_CONTAINER_TODO
    snmp_log(LOG_ERR, "errorTable_extract_index index list not implemented!\n");
    return 0;
#else
    var_errorID.next_variable = NULL;
#endif

    /*
     * parse the oid into the individual index components
     */
    err = parse_oid_indexes(hdr->oids, hdr->len, &var_errorID);
    if (err == SNMP_ERR_NOERROR)
    {
        /*
        * copy index components into the context structure
        */
        ctx->errorID = *var_errorID.val.integer;

        /*
            * TODO: check index for valid values. For EXAMPLE:
            *
              * if ( *var_errorID.val.integer != XXX ) {
          *    err = -1;
          * }
          */
    }

    /*
     * parsing may have allocated memory. free it.
     */
    snmp_reset_var_buffers(&var_errorID);

    return err;
}

