/************************************************************
 * the *_duplicate row routine
 */
errorTable_context *
errorTable_duplicate_row(errorTable_context *row_ctx)
{
    errorTable_context *dup;

    if (!row_ctx)
        return NULL;

    dup = SNMP_MALLOC_TYPEDEF(errorTable_context);
    if (!dup)
        return NULL;

    if (errorTable_row_copy(dup, row_ctx))
    {
        free(dup);
        dup = NULL;
    }

    return dup;
}

