genericTypesTable_context *genericTypesTable_create_row(netsnmp_index *hdr, genericTypeStruct req)
{
    genericTypesTable_context *ctx = SNMP_MALLOC_TYPEDEF(genericTypesTable_context);
    if (!ctx)
        return NULL;
    if (genericTypesTable_extract_index(ctx, hdr))
    {
        free(ctx->index.oids);
        free(ctx);
        return NULL;
    }
    ctx->index.oids = ctx->oid_buf;
    ctx->oid_buf[0] = req.genericTypeID;
    ctx->index.len = 1;
    ctx->genericTypeID = (long unsigned int)req.genericTypeID;
    strcpy(ctx->typeDescription, req.typeDescription);
    ctx->typeDescription_len = strlen(req.typeDescription);
    return ctx;
}

