/************************************************************
 * the *_duplicate row routine
 */
genericTypesTable_context *
genericTypesTable_duplicate_row(genericTypesTable_context *row_ctx)
{
    genericTypesTable_context *dup;

    if (!row_ctx)
        return NULL;

    dup = SNMP_MALLOC_TYPEDEF(genericTypesTable_context);
    if (!dup)
        return NULL;

    if (genericTypesTable_row_copy(dup, row_ctx))
    {
        free(dup);
        dup = NULL;
    }

    return dup;
}
/************************************************************
 * the *_row_copy routine
 */
int genericTypesTable_row_copy(genericTypesTable_context *dst, genericTypesTable_context *src)
{
    if (!dst || !src)
        return 1;

    /*
     * copy index, if provided
     */
    if (dst->index.oids)
        free(dst->index.oids);
    if (snmp_clone_mem((void *)&dst->index.oids, src->index.oids,
                       src->index.len * sizeof(oid)))
    {
        dst->index.oids = NULL;
        return 1;
    }
    dst->index.len = src->index.len;

    /*
     * copy components into the context structure
     */
    dst->genericTypeID = src->genericTypeID;

    memcpy(dst->typeDescription, src->typeDescription, src->typeDescription_len);
    dst->typeDescription_len = src->typeDescription_len;

    return 0;
}
