/************************************************************
 * Initializes the mapTypeTable module
 * TODO: Find solution for DM messages, atm they are ignored
 */
void init_mapTypeTable(BO_List *boList)
{
    mapTypeTable_context *ctx;
    netsnmp_index index;
    oid index_oid[2];

    sampleUnitsList sampleunitsList;
    sampleunitsList.capacity = 1;
    sampleunitsList.current = 0;
    sampleunitsList.list = malloc(sizeof(sampleUnitsStruct));

    genericTypeList typesList;
    typesList.capacity = 1;
    typesList.current = 0;
    typesList.genericList = malloc(sizeof(genericTypeList));
    initialize_table_mapTypeTable();
    int inserted = 0;
    int errorInserted = 0;
    regex_t regex;
    /*This regex will ignore Diagnostic Messages, that is messages whose name is DMxxx, since I don't have the tools
      to properly test the accuracy of the decoder when it comes to those messages*/
    int value = regcomp(&regex, "DM[0-9]+:", REG_EXTENDED);
    if (value)
    {
        fprintf(stderr, "Could not compile regex\n");
        exit(1);
    }
    for (int i = 0; i < boList->current; i++)
    {
        value = regexec(&regex, boList->list[i].name, 0, NULL, 0);
        if (value == REG_NOMATCH)
        {
            for (int j = 0; j < boList->list[i].signals->current; j++)
            {
                int idSampleUnits = addToSampleUnits(&sampleunitsList, boList->list[i].messageID, boList->list[i].signals->list[j].unit);
                int idGenericTypes = addToGenericTypes(&typesList, boList->list[i].messageID, boList->list[i].signals->list[j].description);
                index_oid[0] = inserted;
                index.oids = (oid *)&index_oid;
                index.len = 1;
                char *aux = malloc(sizeof(char) + strlen(boList->list[i].signals->list[j].name) + strlen(boList->list[i].name) + 1);
                strcpy(aux, boList->list[i].name);
                strcat(aux, boList->list[i].signals->list[j].name);
                ctx = NULL;
                /* Search for it first. */
                ctx = CONTAINER_FIND(cb.container, &index);
                if (!ctx)
                {
                    // No dice. We add the new row
                    ctx = mapTypeTable_create_row(&index, aux, "CAN 2.0", inserted, idGenericTypes, idSampleUnits, 0, 0, 0, 0);
                    CONTAINER_INSERT(cb.container, ctx);
                    inserted++;
                }
                free(aux);
            }
        }
    }
    init_sampleUnitsTable(&sampleunitsList);
    init_genericTypesTable(&typesList);
    free(sampleunitsList.list);
    free(typesList.genericList);
    regfree(&regex);
}

