/************************************************************
 * mapTypeTable_get_value
 *
 * This routine is called for get requests to copy the data
 * from the context to the varbind for the request. If the
 * context has been properly maintained, you don't need to
 * change in code in this fuction.
 */
int mapTypeTable_get_value(
    netsnmp_request_info *request,
    netsnmp_index *item,
    netsnmp_table_request_info *table_info)
{
    netsnmp_variable_list *var = request->requestvb;
    mapTypeTable_context *context = (mapTypeTable_context *)item;

    switch (table_info->colnum)
    {

    case COLUMN_MAPTYPEID:
        /** UNSIGNED32 = ASN_UNSIGNED */
        snmp_set_var_typed_value(var, ASN_UNSIGNED,
                                 (char *)&context->mapTypeID,
                                 sizeof(context->mapTypeID));
        break;

    case COLUMN_PROPRIETARYTYPEID:
        /** UNSIGNED32 = ASN_UNSIGNED */
        snmp_set_var_typed_value(var, ASN_UNSIGNED,
                                 (char *)&context->proprietaryTypeID,
                                 sizeof(context->proprietaryTypeID));
        break;

    case COLUMN_GENERICMAPTYPEID:
        /** UNSIGNED32 = ASN_UNSIGNED */
        snmp_set_var_typed_value(var, ASN_UNSIGNED,
                                 (char *)&context->genericMapTypeID,
                                 sizeof(context->genericMapTypeID));
        break;

    case COLUMN_SAMPLEUNITMAPID:
        /** UNSIGNED32 = ASN_UNSIGNED */
        snmp_set_var_typed_value(var, ASN_UNSIGNED,
                                 (char *)&context->sampleUnitMapID,
                                 sizeof(context->sampleUnitMapID));
        break;

    case COLUMN_PRECISION:
        /** INTEGER = ASN_INTEGER */
        snmp_set_var_typed_value(var, ASN_INTEGER,
                                 (char *)&context->precision,
                                 sizeof(context->precision));
        break;

    case COLUMN_MAXSAMPLINGFREQUENCY:
        /** UNSIGNED32 = ASN_UNSIGNED */
        snmp_set_var_typed_value(var, ASN_UNSIGNED,
                                 (char *)&context->maxSamplingFrequency,
                                 sizeof(context->maxSamplingFrequency));
        break;

    case COLUMN_MAXMAPDELAY:
        /** INTEGER = ASN_INTEGER */
        snmp_set_var_typed_value(var, ASN_INTEGER,
                                 (char *)&context->maxMapDelay,
                                 sizeof(context->maxMapDelay));
        break;

    case COLUMN_DATASOURCE:
        /** OCTETSTR = ASN_OCTET_STR */
        snmp_set_var_typed_value(var, ASN_OCTET_STR,
                                 (char *)&context->dataSource,
                                 context->dataSource_len);
        break;

    case COLUMN_INTERFACESOURCE:
        /** OCTETSTR = ASN_OCTET_STR */
        snmp_set_var_typed_value(var, ASN_OCTET_STR,
                                 (char *)&context->interfaceSource,
                                 context->interfaceSource_len);
        break;

    default: /** We shouldn't get here */
        snmp_log(LOG_ERR, "unknown column in "
                          "mapTypeTable_get_value\n");
        return SNMP_ERR_GENERR;
    }
    return SNMP_ERR_NOERROR;
}

