/************************************************************
 * the *_create_row routine is called by the table handler
 * to create a new row for a given index. If you need more
 * information (such as column values) to make a decision
 * on creating rows, you must create an initial row here
 * (to hold the column values), and you can examine the
 * situation in more detail in the *_set_reserve1 or later
 * states of set processing. Simple check for a NULL undo_ctx
 * in those states and do detailed creation checking there.
 *
 * returns a newly allocated mapTypeTable_context
 *   structure if the specified indexes are not illegal
 * returns NULL for errors or illegal index values.
 */
mapTypeTable_context *mapTypeTable_create_row(netsnmp_index *hdr, char *data, char *interface, int id, int generic, int sampleunit, int proprietary,
                                              int precision, int maxsample, int maxdelay)
{
    mapTypeTable_context *ctx = SNMP_MALLOC_TYPEDEF(mapTypeTable_context);
    if (!ctx)
        return NULL;
    if (mapTypeTable_extract_index(ctx, hdr))
    {
        free(ctx->index.oids);
        free(ctx);
        return NULL;
    }
    ctx->index.oids = ctx->oid_buf;
    ctx->oid_buf[0] = id;
    ctx->index.len = 1;
    ctx->mapTypeID = (long unsigned int)id;
    ctx->genericMapTypeID = generic;
    ctx->sampleUnitMapID = sampleunit;
    ctx->proprietaryTypeID = proprietary;
    ctx->precision = precision;
    ctx->maxSamplingFrequency = maxsample;
    ctx->maxMapDelay = maxdelay;
    strcpy(ctx->dataSource, data);
    ctx->dataSource_len = strlen(data);
    strcpy(ctx->interfaceSource, interface);
    ctx->interfaceSource_len = strlen(interface);
    return ctx;
}

/*
 * the *_extract_index routine
 */
int mapTypeTable_extract_index(mapTypeTable_context *ctx, netsnmp_index *hdr)
{
    /*
     * temporary local storage for extracting oid index
     */
    netsnmp_variable_list var_mapTypeID;
    int err;

    /*
     * copy index, if provided
     */
    if (hdr)
    {
        netsnmp_assert(ctx->index.oids == NULL);
        if (snmp_clone_mem((void *)&ctx->index.oids, hdr->oids, sizeof(oid)))
        {
            return -1;
        }
    }

    /**
     * Create variable to hold each component of the index
     */
    memset(&var_mapTypeID, 0x00, sizeof(var_mapTypeID));
    var_mapTypeID.type = ASN_GAUGE;
    /** TODO: link this index to the next, or NULL for the last one */
#ifdef TABLE_CONTAINER_TODO
    snmp_log(LOG_ERR, "mapTypeTable_extract_index index list not implemented!\n");
    return 0;
#else
    var_mapTypeID.next_variable = NULL;
#endif

    /*
     * parse the oid into the individual components
     */
    err = parse_oid_indexes(hdr->oids, hdr->len, &var_mapTypeID);
    if (err == SNMP_ERR_NOERROR)
    {
        /*
        * copy components into the context structure
        */
        /** skipping external index mapTypeID */
        if (var_mapTypeID.val_len > sizeof(ctx->mapTypeID))
            err = -1;
        else
            ctx->mapTypeID = (long unsigned int)var_mapTypeID.val.integer;

        /*
            * TODO: check index for valid values. For EXAMPLE:
            *
              * if ( XXX_check_value( var_mapTypeID.val, XXX ) ) {
          *    err = -1;
          * }
          */
    }

    /*
     * parsing may have allocated memory. free it.
     */
    snmp_reset_var_buffers(&var_mapTypeID);

    return err;
}
/************************************************************
 * the *_duplicate row routine
 */
mapTypeTable_context *mapTypeTable_duplicate_row(mapTypeTable_context *row_ctx)
{
    mapTypeTable_context *dup;

    if (!row_ctx)
        return NULL;

    dup = SNMP_MALLOC_TYPEDEF(mapTypeTable_context);
    if (!dup)
        return NULL;

    if (mapTypeTable_row_copy(dup, row_ctx))
    {
        free(dup);
        dup = NULL;
    }

    return dup;
}
/************************************************************
 * the *_row_copy routine
 */
int mapTypeTable_row_copy(mapTypeTable_context *dst, mapTypeTable_context *src)
{
    if (!dst || !src)
        return 1;

    /*
     * copy index, if provided
     */
    if (dst->index.oids)
        free(dst->index.oids);
    if (snmp_clone_mem((void *)&dst->index.oids, src->index.oids,
                       src->index.len * sizeof(oid)))
    {
        dst->index.oids = NULL;
        return 1;
    }
    dst->index.len = src->index.len;
    dst->mapTypeID = src->mapTypeID;
    dst->proprietaryTypeID = src->proprietaryTypeID;
    dst->genericMapTypeID = src->genericMapTypeID;
    dst->sampleUnitMapID = src->sampleUnitMapID;
    dst->precision = src->precision;
    dst->maxSamplingFrequency = src->maxSamplingFrequency;
    dst->maxMapDelay = src->maxMapDelay;

    memcpy(dst->dataSource, src->dataSource, src->dataSource_len);
    dst->dataSource_len = src->dataSource_len;
    memcpy(dst->interfaceSource, src->interfaceSource, src->interfaceSource_len);
    dst->interfaceSource_len = src->interfaceSource_len;
    return 0;
}